(window.webpackJsonp=window.webpackJsonp||[]).push([[65],{629:function(t,n,v){"use strict";v.r(n);var i=v(16),_=Object(i.a)({},(function(){var t=this,n=t.$createElement,v=t._self._c||n;return v("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[v("p",[t._v("#前言\n")]),v("div",{staticStyle:{width:"50%",float:"left"}},[t._v("\n本学时主要学习项目管理基础、\n项目管理知识体系构成、IPMA和IPMP、\nPRINCE2、组织结构对项目的影响、信息系统项目的生命周期、信息系统项目典型生命周期模型、单个项目的管理过程等的内容。\n根据考试大纲，本章知识点会涉及单选题型，约占6～12分。本章节内容偏重于概念知识，根据以往全国计算机技术与软件专业技术资格（水平）考试的出题规律而言，概念知识考查知识点多数参照教材，扩展内容较少。\n")]),t._v(" "),v("div",{staticStyle:{width:"50%",float:"left"}},[v("img",{attrs:{src:"https://cdn.jsdelivr.net/gh/julie7366/picgo-blog/images/infoSystem/%E5%9B%BE1-1.jpg",alt:"图1-1"}})]),v("p"),t._v(" "),v("h2",{attrs:{id:"_1-1项目管理基础"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_1-1项目管理基础"}},[t._v("#")]),t._v(" 1.1\t项目管理基础")]),t._v(" "),v("p",[t._v("【基础知识点】")]),t._v(" "),v("ol",[v("li",[t._v("项目是为提供一项独特产品、服务或成果所做的临时性努力。")]),t._v(" "),v("li",[t._v("项目的具体特点如下：临时性（一次性）；独特的产品、服务和成果；逐步完善；资源约束；目的性。")]),t._v(" "),v("li",[t._v("项目工作的三个主要目标（三约束）：时间、成本和质量。")]),t._v(" "),v("li",[t._v("项目经理的责任就是在时间、成本、质\n量和项目范围之间进行权衡以保证项目的成功。")]),t._v(" "),v("li",[t._v("日常运作和项目之间的区分（见表1-1）\n(1) 日常运作是持续不断和重复进行的，而项目时临时性的、独特的。"),v("br"),t._v("\n(2) 项目和日常运作的目标有本质的不同。项目的目标是实现其目标，然后结束项目而持续进行的日常运作的目标一般是为了维持经营。"),v("br"),t._v("\n(3) 项目的实现机制与日常运作大相径庭， 因为当宣布的目标实现时，项目即结束。")])]),t._v(" "),v("p",[t._v("表1-1\t项目和日常运作的区别\n"),v("img",{attrs:{src:"https://cdn.jsdelivr.net/gh/julie7366/picgo-blog/images/infoSystem/%E8%A1%A81-1.jpg",alt:"项目和日常运作的区别"}})]),t._v(" "),v("ol",{attrs:{start:"5"}},[v("li",[t._v("项目管理就是把各种知识、技能、手段和技术应用于\n项目活动之中，以达到项目的要求。")]),t._v(" "),v("li",[t._v("项目管理是通过应用和综合诸如启动、计划、实施、监控和收尾等项目管理过程来进行的。")]),t._v(" "),v("li",[t._v("管理一个项目包括：识别要求；确定清楚而又能够实现的目标；权衡质量、范围、时间和成本方面互不相让的要求；使技术规格说明书、计划和方法适合于各种各样项目干系人的不同需求与期望等内容。")]),t._v(" "),v("li",[t._v("项目团队应当将项目置于其所处的文化、社会、国际、政治和自然的环节及其同这些环境之间的关系中加以考虑：文化与社会环境；国际与政治环境；自然环境。")])]),t._v(" "),v("h2",{attrs:{id:"_1-2项目管理知识体系构成"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_1-2项目管理知识体系构成"}},[t._v("#")]),t._v(" 1.2\t项目管理知识体系构成")]),t._v(" "),v("p",[t._v("【基础知识点】")]),t._v(" "),v("ol",[v("li",[t._v("有效的管理要求项目管理组至少能理解和使用以下五方面的专门知识领域：项目管理知识体系；应用领域的知识、标准和规定；项目环境知识；通用的管理知识和技能；软技能或人际关系技能。（见图1-2）")])]),t._v(" "),v("p",[t._v("图1-2： 项目管理组需要的知识领域\n"),v("img",{attrs:{src:"https://cdn.jsdelivr.net/gh/julie7366/picgo-blog/images/infoSystem/%E5%9B%BE1-2.jpg",alt:"项目管理组需要的知识领域"}}),t._v("\n2. 国际标准化组织（ISO）是这样区分标准和规则的\n（ISO，1994）：\n标准是“一致同意建立并由公认的机构批准的文件，该团体提供通用的和可重复使用的规则、指南、活动或其结果的特征”；\n规则是政府强制的要求；标准和规则之间有很大的一块灰色区；\n标准通常以描述一项为多数人选用的最佳方案的准则形式开始，然后随着其得到广泛的采用，变成了实际的规则；\n可以在不同的组织层次上规定要强制遵守，例如由政府机构、执行组织的管理层、项目管理团队建立的特定政策和规程。\n3. 项目管理团队应该在项目的社会、政治和自然环境背景下来考虑该项目。")]),t._v(" "),v("h2",{attrs:{id:"_1-3ipmp-pmp"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_1-3ipmp-pmp"}},[t._v("#")]),t._v(" 1.3\tIPMP/PMP")]),t._v(" "),v("p",[t._v("【基础知识点】")]),t._v(" "),v("ol",[v("li",[t._v("国际项目管理协会（International Project Management Association，\nIPMA）创建于1965年，非赢利性专业性国际学术组织。最初成员多为欧洲国家，现已扩展到世界各大洲。")]),t._v(" "),v("li",[t._v("国际项目管理资质标准（IPMA Competence Baseline，ICB）是IPMA 建立额知识体系。划分为28个核心要素和14个附加要素。如表1-2所示：")])]),t._v(" "),v("p",[v("img",{attrs:{src:"https://cdn.jsdelivr.net/gh/julie7366/picgo-blog/images/infoSystem/%E8%A1%A81-2.jpg",alt:"xxx"}})]),t._v(" "),v("ol",{attrs:{start:"3"}},[v("li",[t._v("国际项目管理专业资质认证（International Project Management\nProfessional，IPMP）是IPMA在全球推行的四级项目管理专业资质认证体系的   总称。")]),t._v(" "),v("li",[t._v("依据国际项目管理专业资质标准，针对项目管理人员专业水平的不同将项目管理专业人员资质认证划分为四个等级，即A级、B级、C级、D级，每个等级授予不同级别的证书。")])]),t._v(" "),v("ul",[v("li",[t._v("A级(IPMA Level A)证书是国际特级项目经理( Certified Projects Director)\n获得这一级认证的项目管理专业人员有能力指导一个公司(或一个分支机构)的包括有诸多项目的复杂规划，有能力管理该组织的所有项目，或者管理一项国际合作的复杂项目。")]),t._v(" "),v("li",[t._v("B级(IPMA Level B)证书是国际高级项目经理(Certified Senior Project Manager) 获得这一级认证的项目管理专业人员可以管理大型复杂项目，或者管理一项国际合作项目。")]),t._v(" "),v("li",[t._v("C级(IPMA Level C)证书是国际项目经理(Certified Project Manager)\n获得这一级认证的项目管理专业人员能够管理一般复杂项目，也可以在所在项目中辅助高级项目经理进行管理。")]),t._v(" "),v("li",[t._v("D级(IPMA Level D)证书是国际助理项目经理(Certified Project Management Associate)\n获得这一级认证的项目管理人员具有项目管理从业的基本知识，并可以将它们应用于某些领域。")])]),t._v(" "),v("p",[t._v("国 际 项 目 经 理 资 质 认 证 文 件\n关于调整IPMP认证费的公告"),v("br"),t._v("\n结合当前国内外市场经济环境，经中国（双法）项目管理研究委员会、\nIPMP中国认证委员会研究决定：自2016年4月1日起调整IPMP（中国区）认证   费收费标准。新的IPMP认证收费标准如下：")]),t._v(" "),v("p",[t._v("特别说明：以上所有价格仅限国内中文认证。国外认证、国内英文认证每人增加1000元/人。")]),t._v(" "),v("ol",{attrs:{start:"5"}},[v("li",[t._v("项目管理知识体系（Project Management Body of Knowledge，\nPMBOK），是早在20世纪70年代末提出的。")]),t._v(" "),v("li",[t._v("PMBOK指南每四年更新一次，2018年为第6版。分为10个知识领域：即范围   管理、时间管理、成本管理、质量管理、人力资源管理、沟通管理、干系人管理、采购管理、风险管理和整体管理。")]),t._v(" "),v("li",[t._v("目前，PMP认证只有一个级别对参加PMP认证学员资格的要求与IPMA的C级相当。证书有效期为三年。")])]),t._v(" "),v("h2",{attrs:{id:"_1-4prince2"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_1-4prince2"}},[t._v("#")]),t._v(" 1.4\tPRINCE2")]),t._v(" "),v("p",[t._v("【背景】\nPRINCE2 是Project IN Controlled Environment (受控环境下的项目管理)的简称。PRINCE2是这种方法的第二个重要版本，由英国政府商   务部(OGC)所有，于1996年开始推广。\nPRINCE2描述了如何以一种逻辑性的、有组织的方法，按照明确的  步骤对项目进行管理。\n它不是一种工具也不是一种技巧，而是结构化的项目管理流程。这也是为什么它容易被调整和升级，适用于所有类型的项目和情况。经过大量实践检验的PRINCE2 能够有效提高项目执行的效率和效益。")]),t._v(" "),v("p",[t._v("【基础知识点】\n")]),v("div",{staticStyle:{width:"50%",float:"left"}},[v("p"),t._v(" "),v("ol",[v("li",[t._v("PRINCE2认证在国际上被称为项目管理王者认证。")]),t._v(" "),v("li",[t._v("PRINCE2是一种基于流程的结构化项目管理方法。")]),t._v(" "),v("li",[t._v("PRINCE2的原则、主题和流程与《PMBOK》指南一致，但PRINCE2  不包含《PMBOK》指南中所有知识点和细节。")]),t._v(" "),v("li",[t._v("PRINCE2旨在以一种适于广泛项目环境的方式组织安排和着重介绍项  目管理知识。")]),t._v(" "),v("li",[t._v("PRINCE2假设了解和采用该方法的用户具有一定的经验，能够自行填  补其省略的细节。")]),t._v(" "),v("li",[t._v("PRINCE2四要素包括：原则、流程、\n主题和项目环境。如图1-3描述。")]),t._v(" "),v("li",[t._v("PRINCE2方法的七个“原则”：持 续业务验证；吸取经验教训；明确定 义的角色和职责；按阶段管理；例外 管理；关注产品；根据项目环境剪裁。")]),t._v(" "),v("li",[t._v("PRINCE2主题包括：商业论证；组 织；质量；计划；风险；变更；进展。")])])]),t._v(" "),v("div",{staticStyle:{width:"50%",float:"left"}},[v("p",[v("img",{attrs:{src:"https://cdn.jsdelivr.net/gh/julie7366/picgo-blog/images/infoSystem/%E5%9B%BE1-3.jpg",alt:"主题和项目环境"}})])]),t._v(" "),v("div",{staticStyle:{clear:"both"}}),t._v("\n【扩展】\nPRINCE2资格考试:\nPRINCE2 资格证书分为两个级别:基础级别(Foundation)和实践者级(Practitioner)。\n- 基础资格(Foundation)\nFoundation考试是要获得PRINCE2® 从业资格证书必须通过的两个考试的第一个。考生需要展示其对PRINCE2® 方法论原理和术语的理解。Foundation考题形式:为单项选择;考试时间为1小时的闭卷考试;75道题，5道测试题，满分70分，35道正确为及格。\n"),v("ul",[v("li",[t._v("实践者资格(Practitioner)\nPractitioner考试是要获得PRINCE2® 从业资格证书必须通过的两个考试的第二个。考生需要展示其在特定项目环境和项目情节中对PRINCE2方法论的应用和灵活使用。考题格式:9道问题，提供一个完整的项目管理案例，包括项目环境，情节背景和附加信息; 每道题值12分，\n59分(总分108分)及格;考试时间为1.5小时开卷考试(只允许携带PRINCE2手册作参考)。")])]),t._v(" "),v("p",[t._v("【扩展】\n学习费用")]),t._v(" "),v("ul",[v("li",[t._v("PRINCE2 Foundation基础级 培训费3980\t考试费2300")]),t._v(" "),v("li",[t._v("PRINCE2 Practitioner专业级 培训费4800\t考试费 4100")]),t._v(" "),v("li",[t._v("特别说明：根据英国OGC/APMG的规定，学员必须先取得PRINCE2\nFoundation基础级证书才有资格获取PRINCE2 Practitioner专业级证书。")])]),t._v(" "),v("p",[t._v("允许一天之内完成两级考试，基础级考试成绩在考试结束后当时产生，专业级成绩四周后产生，证书在考试六周后寄到。")]),t._v(" "),v("p",[t._v("【基础知识点】")]),t._v(" "),v("ol",[v("li",[t._v("项目管理委员会的活动涵盖在项目指导流程中，该流程从项目之前就开始，一直到并包括最终阶段。")]),t._v(" "),v("li",[t._v("PRINCE2包括以下流程：项目准备流程；项目指导流程；项目启动流程；阶段控制流程；阶段边界管理；产品交付管理流程；项目收尾流程。")])]),t._v(" "),v("h2",{attrs:{id:"_1-5组织结构对项目的影响"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_1-5组织结构对项目的影响"}},[t._v("#")]),t._v(" 1.5\t组织结构对项目的影响")]),t._v(" "),v("p",[t._v("【基础知识点】")]),t._v(" "),v("ol",[v("li",[v("p",[t._v("以项目为基础的组织是指他们的业务主要由项目组成，分为两大类：\n（1）其主要收入是源自依照合同为他人履行项目的组织；\n（2）采用项目制进行管理的组织。")])]),t._v(" "),v("li",[v("p",[t._v("组织结构\n与项目有关的主要企业组织结构类型的关键特征，如图1-4所示。")])])]),t._v(" "),v("p",[t._v("图1-4：组织结构对项目的影响\n"),v("img",{attrs:{src:"https://cdn.jsdelivr.net/gh/julie7366/picgo-blog/images/infoSystem/%E5%9B%BE1-4.jpg",alt:"组织结构对项目的影响"}}),t._v("\n3. 职能型组织（图1-5）\n"),v("img",{attrs:{src:"https://cdn.jsdelivr.net/gh/julie7366/picgo-blog/images/infoSystem/%E5%9B%BE1-5.jpg",alt:"xxx"}})]),t._v(" "),v("p",[t._v("职能型组织优点：\n①便于知识、技能和经验交流；\n②清晰的职业生涯晋升路线；\n③沟通、交流简单、责任和权限清晰；\n④重复性工作为主的过程管理。缺点：\n①职能利益优先于项目；\n②组织横向之间联系薄弱，部门间沟通、协调难度大；\n③项目经理缺少权利、权威；\n④项目管理发展方向不明，缺少项目基准。")]),t._v(" "),v("ol",{attrs:{start:"4"}},[v("li",[t._v("项目型组织（图1-6）\n"),v("img",{attrs:{src:"https://cdn.jsdelivr.net/gh/julie7366/picgo-blog/images/infoSystem/%E5%9B%BE1-6.jpg",alt:"xxx"}})])]),t._v(" "),v("p",[t._v("项目型组织优点：\n①责权分明，利于统一指挥；\n②目标明确单一；\n③沟通简洁、方便；\n④决策快。缺点：\n①管理成本过高；\n②项目环境比较封闭，不利于沟通、技术知识共享；\n③员工缺乏事业上的连续性和保障。")]),t._v(" "),v("ol",{attrs:{start:"5"}},[v("li",[t._v("弱矩阵型组织（图1-7）\n"),v("img",{attrs:{src:"https://cdn.jsdelivr.net/gh/julie7366/picgo-blog/images/infoSystem/%E5%9B%BE1-7.jpg",alt:"xxx"}})]),t._v(" "),v("li",[t._v("平衡矩阵型组织（图1-8）\n"),v("img",{attrs:{src:"https://cdn.jsdelivr.net/gh/julie7366/picgo-blog/images/infoSystem/%E5%9B%BE1-8.jpg",alt:"xxx"}})]),t._v(" "),v("li",[t._v("强矩阵型组织（图1-9）\n"),v("img",{attrs:{src:"https://cdn.jsdelivr.net/gh/julie7366/picgo-blog/images/infoSystem/%E5%9B%BE1-9.jpg",alt:"xxx"}}),t._v("\n矩阵型组织优点：\n①项目经理责任制、有明确的项目目标；②改善了项目经理对整体资 源的控制；③及时影响；④获得职能组织更多的支持；⑤最大限度地 利用公司的稀缺资源；⑥降低了跨职能部门间的协调合作难度；⑦使 质量、成本、时间等制约因素得到更好的平衡；⑧团队成员有归属感， 士气高，问题少；⑨出现的冲突少，且易处理解决。")])]),t._v(" "),v("p",[t._v("矩阵型组织缺点：\n①管理成本增加；②多头领导；③难以监测和控制；④资源分配与项目优先的问题产生冲突；⑤权利难以保持平衡。")]),t._v(" "),v("ol",{attrs:{start:"8"}},[v("li",[t._v("复合型组织（图1-10）\n根据工作需要，一个组织内在运作项目时，或多或少同时包含上述几种组织形式，这就构成了复合型组织结构。\n"),v("img",{attrs:{src:"https://cdn.jsdelivr.net/gh/julie7366/picgo-blog/images/infoSystem/%E5%9B%BE1-10.jpg",alt:"xxx"}})])]),t._v(" "),v("h2",{attrs:{id:"_1-6信息系统项目的生命周期"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_1-6信息系统项目的生命周期"}},[t._v("#")]),t._v(" 1.6\t信息系统项目的生命周期")]),t._v(" "),v("p",[t._v("【基础知识点】")]),t._v(" "),v("ol",[v("li",[t._v("通用的生命周期结构具有以下特征：\n（1）成本与人力投入在开始时较低，在工作执行期间达到最高并在项目快要结束时迅速回落。如图1-1\n1所示。\n"),v("img",{attrs:{src:"https://cdn.jsdelivr.net/gh/julie7366/picgo-blog/images/infoSystem/%E5%9B%BE1-11.jpg",alt:"xxx"}}),t._v("\n（2）风险与不确定性在项目开始时最大，并在项目的整个生命周期中\n随着决策的制定与可交付成果的验收而逐步降低。图1-12。\n"),v("img",{attrs:{src:"https://cdn.jsdelivr.net/gh/julie7366/picgo-blog/images/infoSystem/%E5%9B%BE1-12.jpg",alt:"xxx"}})]),t._v(" "),v("li",[t._v("阶段与阶段的关系有两种基本类型：\n顺序关系、交叠关系")])]),t._v(" "),v("p",[t._v("在交叠关系中，一个阶段在前一个阶段完成前就开始，可作为进度压缩的一种技术，被称为“快速跟进”。")]),t._v(" "),v("p",[t._v("阶段交叠可能需要增加额外的资源来并行开展工作，可能增加风险，也可能因尚未获得前一阶段的准确信息就开始后续工作而造成返工。\n"),v("img",{attrs:{src:"https://cdn.jsdelivr.net/gh/julie7366/picgo-blog/images/infoSystem/%E5%9B%BE1-13.jpg",alt:"xxx"}})]),t._v(" "),v("h2",{attrs:{id:"_1-7信息系统项目典型生命周期模型"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_1-7信息系统项目典型生命周期模型"}},[t._v("#")]),t._v(" 1.7\t信息系统项目典型生命周期模型")]),t._v(" "),v("p",[t._v("【基础知识点】")]),t._v(" "),v("ol",[v("li",[v("p",[t._v("瀑布模型是一个经典的软件生命周期模式，一般将软件开发分为：\n可行性分析（计划）、需求分析、软件设计（概要设计、详细设计）、编码（含单元测试）、测试、运行维护等六个阶段。如图1-13所示。")])]),t._v(" "),v("li",[v("p",[t._v("螺旋模型是一个演化软件模型，\n将原型实现的迭代特征与线性顺序\n（瀑布）模型中控制的和系统化的方面结合起来。使得软件的增量版本的快速开发成为可能。如图1-14 所示。\n螺旋模型的四个象限分别标志每个 周期所划分的四阶段：制订计划、 风险分析、实施工程和客户评估。 螺旋模型强调了风险分析，特别适 用于庞大而复杂的、高风险的系统。\n"),v("img",{attrs:{src:"https://cdn.jsdelivr.net/gh/julie7366/picgo-blog/images/infoSystem/%E5%9B%BE1-14.jpg",alt:"xxx"}})])]),t._v(" "),v("li",[v("p",[t._v("迭代模型的生命周期四个阶段：\n初始、细化、构造、移交，可进一步描述为周期（Cycle）、阶段（Phase）、   迭代（Iteration）。核心工作流从技术角度描述迭代模型的静态组成部分，包括：业务建模、需求获取、分析与设计、实现、测试、部署。如图1-15所示。\n"),v("img",{attrs:{src:"https://cdn.jsdelivr.net/gh/julie7366/picgo-blog/images/infoSystem/%E5%9B%BE1-15.jpg",alt:"xxx"}})])]),t._v(" "),v("li",[v("p",[t._v("V模型是一个对称的结构，非常明确的表明了测试过程中存在的不同的\n级别，并且非常清晰的描述了这些测试阶段和开发阶段的对应关系。\n1)单元测试。一般由开发人员来执行，首先设定最小的测试单元，然后通过设计相应的测试用例来验证各个单元功能的正确性。\n2)集成测试。主要关注点是系统能够成功编译，实现了主要的业务功能， 系统各个模块之间数据能够正常通信等。\n3)系统测试，验证整个系统是否满足需求规格说明。\n4)验收测试，从用户的角度检查系统是否满足合同中定义的需求或者用户需求。")])]),t._v(" "),v("li",[v("p",[t._v("V模型的特点：\n1)主要思想是开发和测试同等重要，左侧代表开发活动，右侧代表测试活动。\n2)针对每个开发阶段都有一个测试级别与之对应。\n3)测试依旧是开发生命周期中的阶段，与瀑布模型不同的是，有多个测试级别与开发阶段对应。\n4)适用于需求明确和\n需求变更不频繁的情形。")])])]),t._v(" "),v("p",[v("img",{attrs:{src:"https://cdn.jsdelivr.net/gh/julie7366/picgo-blog/images/infoSystem/%E5%9B%BE1-16.jpg",alt:"xxx"}})]),t._v(" "),v("ol",{attrs:{start:"6"}},[v("li",[v("p",[t._v("原型法认为很难一下子全面准确地提出用户需求的情况下，首先不要\n求一定要对系统做全面、详细的调查、分析，而是本着开发人员对用户需求的初步理解，先快速开发一个原型系统，然后通过反复修改来实现用户的最终的系统需求。\n原型应当具备的特点如下：\n1)实际可行\n2)具有最终系统的基本特征\n3)构造方便、快速，造价低。")])]),t._v(" "),v("li",[v("p",[t._v("敏捷开发以用户的需求进化为核心，采用迭代、循序渐进的方法进行\n软件开发。换言之，就是把一个大项目分为多个相互联系、但也可独立运行的小项目，并分别完成，在此过程中软件一直处理可使用状态。")])]),t._v(" "),v("li",[v("p",[t._v("敏捷开发的原则\nØ快速迭代\nØ让测试人员和开发者参与需求讨论\nØ编写可测试的需求文档\nØ多沟通、尽量减少文档\nØ做好产品原型\nØ及早考虑测试")])])]),t._v(" "),v("h2",{attrs:{id:"_1-8单个项目的管理过程"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_1-8单个项目的管理过程"}},[t._v("#")]),t._v(" 1.8\t单个项目的管理过程")]),t._v(" "),v("p",[t._v("【基础知识点】")]),t._v(" "),v("ol",[v("li",[t._v("项目管理各过程组成的5个过程组可以对应到PDCA循环，即戴明环：\n“计划（Plan）--执行（Do）-\n-检查（Check）--行动（Act）” 循环。")])]),t._v(" "),v("p",[t._v("P：计划，设定为了达到目标所必须的方法或标准；\nD：实施，按计划逐步实施具体工作；\nC：确认（检查），确认并检查实施的效果；\nA：措施，确认实际效果与计划差异，并根据需要采取措施。")]),t._v(" "),v("ol",[v("li",[t._v("项目管理过程组包括5个：启动过程组；计划过程组；执行过程组；监督与控\n制过程组；收尾过程组。\n1)启动过程组定义并批准项目或项目阶段，包括“制定项目章程”和“识别项目干系人”两个过程。\n2)计划过程组定义和细化目标，并为实现项目而要达到的目标和完成项目要解决的问题范围而规划必要的行动路线。\n3)执行过程组整合人员和其他资源，在项目的生命期或某个阶段执行项目管理计划。\n4)监督与控制过程组要求定期测量和监控项目绩效情况，识别与项目管理计划的偏差，以便在必要时采取纠正措施，确保项目或阶段目标达成。\n5)收尾过程组正式验收产品、服务或工作成果，有序的结束项目或项目阶段。\n启动各过程 -> 监控各过程（规划各过程，执行各过程）-> 收尾各过程")])]),t._v(" "),v("p",[v("img",{attrs:{src:"https://cdn.jsdelivr.net/gh/julie7366/picgo-blog/images/infoSystem/%E8%A1%A81-3.jpg",alt:"xxx"}})]),t._v(" "),v("h2",{attrs:{id:"_1-9-练习题"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_1-9-练习题"}},[t._v("#")]),t._v(" 1.9 练习题")]),t._v(" "),v("ol",[v("li",[v("p",[t._v("常见的软件开发模型有瀑布模型、演化模型、螺旋模型、喷泉模型等。其中\n（\t） 适用于需求明确或很少变更的项目，（\t）主要用来描述面向对象的软件   开发过程。\nA 瀑布模型\tB 演化模型\nC 螺旋模型\tD 喷泉模型\n解析：常见的软件开发模型有瀑布模型、演化模型、螺旋模型、喷泉模型等。\n瀑布模型适用于需求明确或很少变更的项目，也可用在已有类似项目开发经验的项目上。瀑布模型不灵活，特别是无法解决软件需求不明确问题，由于需求不明确导致的问题有可能在项目后期才能发现，但损失已经造成。\n螺旋模型特别适用于庞大而复杂的、高风险的系统。\n喷泉模型主要用于描述面向对象的开发过程，体现了面向对象开发过程的迭代和连续性。答案：A、D")])]),t._v(" "),v("li",[v("p",[t._v("某软件公司欲开发一个图像处理系统，在项目初期开发人员对需求\n并不确定的情况下，采用 （\t） 方法比较合适。\nA 瀑布式\tB 快速原型\nC 协同开发\tD 形式化")])]),t._v(" "),v("li",[v("p",[t._v("某软件公司欲开发一个图像处理系统，在项目初期开发人员对需求\n并不确定的情况下，采用 （ ） 方法比较合适。\nA 瀑布式\tB 快速原型\nC 协同开发\tD 形式化")])])]),t._v(" "),v("p",[t._v("解析：快速原型法从需求收集开始，开发者和客户在一起定义软件的总体目标，标识出已知的需求，并规划出需要进一步定义的区域。它适用于对需求并不确定的情况。\n在需求明确和稳定前提下，才能使用瀑布式模型开发项目。答案：B")]),t._v(" "),v("ol",{attrs:{start:"3"}},[v("li",[t._v("螺旋模型是一种演进式的软件过程模型，结合了原型开发方法的系统性和瀑\n布模型可控性特点。它有两个显著特点，一是采用 （ ） 的方式逐步加深系统定义和实现的深度，降低风险；二是确定一系列 （ ） ，确保项目开发过程中的相关利益者都支持可行的和令人满意的系统解决方案。")])]),t._v(" "),v("p",[t._v("A 逐步交付\tB 顺序\tC 循环\tD 增量\nA 实现方案\tB 设计方案\tC 关键点\tD 里程碑")]),t._v(" "),v("ol",{attrs:{start:"3"}},[v("li",[t._v("螺旋模型是一种演进式的软件过程模型，结合了原型开发方法的系统性和瀑\n布模型可控性特点。它有两个显著特点，一是采用 （ ） 的方式逐步加深系统定义和实现的深度，降低风险；二是确定一系列 （ ） ，确保项目开发过程中的相关利益者都支持可行的和令人满意的系统解决方案。")])]),t._v(" "),v("p",[t._v("A 逐步交付\tB 顺序\tC 循环\tD 增量\t\nA 实现方案\tB 设计方案\t\tC 关键点\tD 里程碑\n解析：螺旋模型将瀑布模型和快速原型模型结合起来，强调了其他模型所忽视 的风险分析，特别适合于大型复杂的系统，它的特点之一是循环反复。在螺旋 模型演进式的过程中，确定一系列的里程碑，以确保项目朝着正确的方向前进， 同时降低风险。\n答案：C、D")]),t._v(" "),v("ol",{attrs:{start:"4"}},[v("li",[t._v("在多年从事信息系统开发的经验基础上，某单位总结了几种典型信\n息系统项目生命周期模型最主要的特点，如下表所示，表中的第一列分别是 （\t） 。\nA ①瀑布模型②迭代模型③螺旋模型B ①迭代模型②瀑布模型③螺旋模型C ①螺旋模型②瀑布模型③迭代模型D ①螺旋模型②迭代模型③瀑布模型")])]),t._v(" "),v("p",[t._v("生命周期模型\t特\t点\n①\t软件开发是一系列的增量发布，逐步产生更完善的版本，强调风险分析\n②\t分阶段进行，一个阶段的工作得到确认后，继续进行下一个阶段，否则返回前一个阶段\n③\t分阶段进行，每个阶段都执行一次传统的、完整的串行过程，其中都包括不同比例的需\n求分析、设计、编码和测试等活动")]),t._v(" "),v("p",[t._v("解析：\n信息系统生命周期模型包括瀑布模型、迭代模型、螺旋模型等。\n瀑布模型中具有以下特点：分阶段进行，一个阶段的工作得到确认后， 继续进行下一个阶段，否则返回前一个阶段。\n在螺旋模型中，软件开发是一系列的增量发布。螺旋模型强调了风险分析，特别适用于庞大而复杂的、高风险的系统。\n迭代模型在大多数传统的生命周期中，分阶段进行，每个阶段都执行一次传统的、完整的串行过程，其中都包括不同比例的需求分析、设计、编码和测试等活动。\n答案：C")]),t._v(" "),v("ol",{attrs:{start:"5"}},[v("li",[v("p",[t._v("螺旋模型的开发过程具有周期性重复的螺旋线状，每个开发周期由4个象限\n组成，分别标志着开发周期的4个阶段。螺旋模型之所以特别适用于庞大而复杂的、高风险的系统开发，是因为它强调其中的 （ ） 阶段。\nA 制订计划\tB 风险分析\nC 实施工程\tD 客户评估解析：\n螺旋模型是将瀑布模型和快速原型模型结合起来，从而强调其他模型所忽视的风险分析，特别适合于大型复杂的系统。螺旋模型沿着螺线进行若干次迭代， 图中的四个象限代表了制订计划、风险分析、实施工程、客户评估四个活动。螺旋模型由风险驱动，强调可选方案和约束条件从而支持软件的重用，有助于将软件质量作为特殊目标融入产品开发之中。所以，正确选项是B。\n答案：B")])]),t._v(" "),v("li",[v("p",[t._v("项目基本特点中，“项目的临时性”含义是指哪一项\nA．项目的工期短\nB．每个项目都具有明确的开始和结束时间或标志\nC．项目未来完成时间未定\nD．项目可以随时取消")])])]),t._v(" "),v("p",[t._v("解析：临时性。所有的项目都是临时的，有明确的开始和结尾，并以实现特定的目标为宗旨，而这个目标也构成了衡量项目成败的客观标准。答案：B")]),t._v(" "),v("ol",{attrs:{start:"7"}},[v("li",[t._v("在大型项目或多项目实施的过程中，负责实施的项目经理对这些项目大都采\n用（1）的方式。投资大、建设周期长、专业复杂的大型项目最好采用 （2） 的组织形式或近似的组织形式。")])]),t._v(" "),v("p",[t._v("（1）A．直接管理\tB．间接管理\tC．水平管理\tD．垂直管理\n（2）A．项目型\tB．职能型\tC．弱矩阵型\tD．直线型")]),t._v(" "),v("p",[t._v("解析：由于大型项目大多数可以分解为多个相互关联的、较小的项目来进行管理，较小的项目由其项目经理直接管理，而大型项目经理通过领导和管理多个较小项目的项目经理，实现对多个较小项目的“间接管理”。所以，第1个的正确选项应该是B。工作周期长、专业复杂或比较特殊的项目，投资或工程量较大的信息系统项目适宜采用强矩阵式或项目式的组织形式。因此，第2个正确选项应该是A。\n答案：B、A")]),t._v(" "),v("ol",{attrs:{start:"8"}},[v("li",[t._v("项目管理过程可以划分为项目启动、制定项目计划、指导和管理项\n目执行、监督和控制项目工作、项目收尾五个过程组。 （33） 属于指导和管理项目执行过程组。\nA．建立WBS和WBS字典\tB．活动排序\nC．项目质量保证\tD．干系人识别")])]),t._v(" "),v("p",[t._v("解析：A和B是规划过程组D是启动过程组。答案：C")])])}),[],!1,null,null,null);n.default=_.exports}}]);